package jwt

import (
	"crypto/rsa"
	"math/big"
	"testing"
)

var rsaPublicKey1 *rsa.PublicKey
var rsaPublicKey2 *rsa.PublicKey
var rsaPrivateKey1 *rsa.PrivateKey
var rsaPrivateKey2 *rsa.PrivateKey

func init() {
	fromBase10 := func(base10 string) *big.Int {
		i, _ := new(big.Int).SetString(base10, 10)
		return i
	}

	rsaPublicKey1 = &rsa.PublicKey{
		N: fromBase10(""),
		E: 3,
	}
	rsaPrivateKey1 = &rsa.PrivateKey{
		PublicKey: *rsaPublicKey1,
		D:         fromBase10(""),
		Primes: []*big.Int{
			fromBase10(""),
			fromBase10(""),
		},
	}

	rsaPublicKey2 = &rsa.PublicKey{
		N: fromBase10(""),
		E: 3,
	}
	rsaPrivateKey2 = &rsa.PrivateKey{
		PublicKey: *rsaPublicKey2,
		D:         fromBase10(""),
		Primes: []*big.Int{
			fromBase10(""),
			fromBase10(""),
		},
	}
}

func TestRSA(t *testing.T) {
	f := func(signer Signer, verifier Verifier, claims interface{}) {
		t.Helper()

		tokenBuilder := NewBuilder(signer)
		token, _ := tokenBuilder.Build(claims)

		err := verifier.Verify(token.Payload(), token.Signature())
		if err != nil {
			t.Errorf("want no err, got: %#v", err)
		}
	}

	f(
		mustSigner(NewSignerRS(RS256, rsaPrivateKey1)),
		mustVerifier(NewVerifierRS(RS256, rsaPublicKey1)),
		&RegisteredClaims{},
	)
	f(
		mustSigner(NewSignerRS(RS384, rsaPrivateKey1)),
		mustVerifier(NewVerifierRS(RS384, rsaPublicKey1)),
		&RegisteredClaims{},
	)
	f(
		mustSigner(NewSignerRS(RS512, rsaPrivateKey1)),
		mustVerifier(NewVerifierRS(RS512, rsaPublicKey1)),
		&RegisteredClaims{},
	)

	f(
		mustSigner(NewSignerRS(RS256, rsaPrivateKey1)),
		mustVerifier(NewVerifierRS(RS256, rsaPublicKey1)),
		&customClaims{
			TestField: "foo",
		},
	)
	f(
		mustSigner(NewSignerRS(RS384, rsaPrivateKey1)),
		mustVerifier(NewVerifierRS(RS384, rsaPublicKey1)),
		&customClaims{
			TestField: "bar",
		},
	)
	f(
		mustSigner(NewSignerRS(RS512, rsaPrivateKey1)),
		mustVerifier(NewVerifierRS(RS512, rsaPublicKey1)),
		&customClaims{
			TestField: "baz",
		},
	)
}

func TestRSA_InvalidSignature(t *testing.T) {
	f := func(signer Signer, verifier Verifier, claims interface{}) {
		t.Helper()

		tokenBuilder := NewBuilder(signer)
		token, _ := tokenBuilder.Build(claims)

		err := verifier.Verify(token.Payload(), token.Signature())
		if err == nil {
			t.Errorf("want %v, got nil", ErrInvalidSignature)
		}
	}
	f(
		mustSigner(NewSignerRS(RS256, rsaPrivateKey1)),
		mustVerifier(NewVerifierRS(RS256, rsaPublicKey2)),
		&RegisteredClaims{},
	)
	f(
		mustSigner(NewSignerRS(RS384, rsaPrivateKey1)),
		mustVerifier(NewVerifierRS(RS384, rsaPublicKey2)),
		&RegisteredClaims{},
	)
	f(
		mustSigner(NewSignerRS(RS512, rsaPrivateKey1)),
		mustVerifier(NewVerifierRS(RS512, rsaPublicKey2)),
		&RegisteredClaims{},
	)

	f(
		mustSigner(NewSignerRS(RS256, rsaPrivateKey1)),
		mustVerifier(NewVerifierRS(RS256, rsaPublicKey2)),
		&customClaims{
			TestField: "foo",
		},
	)
	f(
		mustSigner(NewSignerRS(RS384, rsaPrivateKey1)),
		mustVerifier(NewVerifierRS(RS384, rsaPublicKey2)),
		&customClaims{
			TestField: "bar",
		},
	)
	f(
		mustSigner(NewSignerRS(RS512, rsaPrivateKey1)),
		mustVerifier(NewVerifierRS(RS512, rsaPublicKey2)),
		&customClaims{
			TestField: "baz",
		},
	)
}
